const onSent = require('./on-sent')
const onReceived = require('./on-received')
const send = require('./send')
const read = require('./read')

function init (deps) {
  return {
    onSent: onSent(deps),
    onReceived: onReceived(deps),
    send: send(deps),
    read: read(deps)
  }
}

module.exports = init
