function init ({ onBroadcast }) {
  function onUserSet (cb) {
    if (typeof cb !== 'function') {
      cb = () => {}
      console.log('WARN', 'must provide function to stream.onUserSet')
    }
    onBroadcast('stream::updated', message => {
      if (message.data.type !== 'setStreamRole') {
        return
      }
      cb(message)
    })
  }
  return onUserSet
}

module.exports = init
