function init ({ onBroadcast }) {
  function onAdminStatusGiven (cb) {
    if (typeof cb !== 'function') {
      cb = () => {}
      console.log('WARN', 'must provide function to team.onAdminStatusGiven')
    }

    onBroadcast('team::admin-status-given', cb)
  }

  return onAdminStatusGiven
}
module.exports = init
