function init ({ onBroadcast }) {
  function onAdminStatusRevoked (cb) {
    if (typeof cb !== 'function') {
      cb = () => {}
      console.log('WARN', 'must provide function to team.onAdminStatusRevoked')
    }
    onBroadcast('team::admin-status-revoked', cb)
  }

  return onAdminStatusRevoked
}
module.exports = init
