function init ({ onBroadcast }) {
  function onStatusUpdated (cb) {
    if (typeof cb !== 'function') {
      cb = () => {}
      console.log('WARN', 'must provide function to thread.onStatusUpdated')
    }
    onBroadcast('thread::updated', message => {
      if (message.data.type !== 'setStatus') return
      cb(message)
    })
  }
  return onStatusUpdated
}

module.exports = init
