/* global test, expect */
const { promisify } = require('util')
const { resolve } = require('path')
let { readdir } = require('fs')
const notification = require('../../lib/notification')
const getMethodsFromFiles = require('../transform')

const libPath = resolve(__dirname, '../../lib/notification')
readdir = promisify(readdir)

test('[NOTIFICATION] returns correct current api', async () => {
  const files = await readdir(libPath)
  const methods = getMethodsFromFiles(files)
  const notificationApi = Object.keys(notification({})).sort()
  expect(notificationApi).toEqual(methods)
})
