/* global test, expect */
const { promisify } = require('util')
const { resolve } = require('path')
let { readdir } = require('fs')
const thread = require('../../lib/thread')
const getMethodsFromFiles = require('../transform')

const libPath = resolve(__dirname, '../../lib/thread')
readdir = promisify(readdir)

test('[THREAD] returns correct current api', async () => {
  const files = await readdir(libPath)
  const methods = getMethodsFromFiles(files)
  const threadApi = Object.keys(thread({})).sort()
  expect(threadApi).toEqual(methods)
})
