function transform (str, val) {
  let newStr = ''
  let i = 0
  while (str[i]) {
    if (str[i] !== val) {
      newStr += str[i]
      i++
      continue
    }
    newStr += ''
    i++
    newStr += str[i].toUpperCase()
    i++
  }
  return newStr
}
function getMethodFromFiles (files) {
  return files.reduce((acc, file) => {
    if (file === 'index.js') return acc
    const method = transform(file.replace('.js', ''), '-')

    return acc.concat(method)
  }, []).sort()
}

module.exports = getMethodFromFiles
